import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import Rating from "@mui/material/Rating";
import Stack from "@mui/material/Stack";

function MovieDetail({data}) {
  const {id} = useParams();
  console.log(id);
  console.log("Data Movie Detail:", data);
  const [detail, setDetail] = useState({});

  useEffect(() => {
    fetch(
      `https://api.themoviedb.org/3/movie/${id}?api_key=131f3dc4657440aa2ed80c0a22a7d1f9&language=en-US`
    )
      .then((res) => res.json())
      .then((res) => {
        setDetail(res);
      })
      .catch((err) => {});
  }, []);
  console.log(detail);

  return (
    <div className="bg-gradient-to-t from-white to-gray-800">
      <div className="container mx-auto">
        <div className="grid grid-cols-2 gap-2 ">
          <div>
            <img
              className="rounded-[4.5rem] mt-[5rem]"
              src={
                "https://www.themoviedb.org/t/p/w600_and_h900_bestv2" +
                detail.poster_path
              }
              alt={detail.title}
            />
          </div>
          <div>
            <div className="grid-rows-1">
              <div>
                <h1 className="mt-10 text-6xl text-white font-extrabold">
                  {detail.title}
                </h1>
              </div>
              <div className="grid-rows-2">
                <div>
                  <p className="text-white mt-2">
                    <span className="">Hello</span>
                    {detail.release_date} - {detail.status} - {detail.tagline}
                  </p>
                </div>

                <Stack className="mt-5" spacing={1}>
                  <Rating
                    name="half-rating-read"
                    defaultValue={detail.vote_average / 2}
                    precision={0.5}
                    readOnly
                    size="large"
                  />
                </Stack>
              </div>
              <div></div>
              <div>
                <h1 className="text-white text-2xl font-semibold mt-5">
                  Overview
                </h1>
                <p className="text-white mt-2">{detail.overview}</p>
              </div>
              <div>Heloooooooooooooooooooooooooooooooooooooooooooooooo</div>
              <div>Heloooooooooooooooooooooooooooooooooooooooooooooooo</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default MovieDetail;
