import { useEffect, useState } from 'react'

const tabs = ['comments', 'posts', 'albums']
function Content() {
  const [post, setPost] = useState([])
  const [type, setType] = useState('comments')

  useEffect(() => {
    fetch(`https://jsonplaceholder.typicode.com/${type}`)
      .then(res => res.json())
      .then(data => {
        setPost(data)
      })
  }, [type])

  return (
    <div>
      {tabs.map(tabs => (
        <button onClick={() => setType(tabs)}> {tabs} </button>
      ))}

      <ul>
        {post.map(data => (
          <li>{data.title || data.name}</li>
        ))}
      </ul>
    </div>
  )
}

export default Content
