import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import Rating from "@mui/material/Rating";
import Stack from "@mui/material/Stack";
import RecommendMovie from "./recommend";
import MovieVideo from "./MovieVideo";
import Cast from "./cast";

function MovieDetail({data}) {
  const {id} = useParams();
  const [detail, setDetail] = useState({});

  useEffect(() => {
    fetch(
      `https://api.themoviedb.org/3/movie/${id}?api_key=131f3dc4657440aa2ed80c0a22a7d1f9&language=en-US`
    )
      .then((res) => res.json())
      .then((res) => {
        setDetail(res);
        window.scrollTo({top: 0, left: 0, behavior: "smooth"});
      })
      .catch((err) => {});
  }, [id]);

  return (
    <div className="bg-gradient-to-t from-white to-gray-800">
      <div className="container mx-auto">
        <div className="grid grid-cols-2 gap-2 ">
          <div>
            <img
              className="rounded-[4.5rem] mt-[5rem]"
              src={
                "https://www.themoviedb.org/t/p/w600_and_h900_bestv2" +
                detail.poster_path
              }
              alt={detail.title}
            />
          </div>
          <div className="self-center">
            <div className="grid-rows-1  ">
              <div>
                <h1 className=" text-[5rem] text-white font-extrabold">
                  {detail.title}
                </h1>
              </div>
              <div className="grid-rows-2">
                <p className="text-white mt-2">
                  {detail.release_date} - {detail.status} - {detail.tagline}
                </p>
              </div>

              <div>
                {(detail.genres || []).map((value) => (
                  <span className="flex flex-row text-white">{value.name}</span>
                ))}
              </div>

              <div>
                <h1 className="text-white text-2xl font-semibold mt-5">
                  Overview
                </h1>
                <p className="text-white mt-2">{detail.overview}</p>
              </div>
              <div className="mt-8 ">
                <a
                  href="#"
                  className="transition ease-in-out delay-150 bg-teal-600 hover:-translate-y-1 hover:scale-110 hover:bg-indigo-500 duration-300 inline-block text-center  border border-transparent rounded-md py-3 px-8 font-medium text-white "
                >
                  {detail.status}
                </a>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="mx-48 mr-48 mt-12">
        <Cast />
      </div>
      <div className="mx-48 mr-48 mt-12">
        <MovieVideo />
      </div>

      <div className="mx-16 mr-16">
        <RecommendMovie className="" />
      </div>
    </div>
  );
}

export default MovieDetail;
