import  { useState }  from 'react'

function App() {
  const gifts = [
    'iphone 13 Pro Max',
    'Macbook Pro M1',
    'Apple Pencil' 
  ]

  const [ gift , setGift ] = useState('Chua co phan thuong')
  function randomGift(){
    setGift(gifts[Math.floor(Math.random() * 3)])
  }
  return (
    <div className="App">
       <h1>{gift}</h1>
       <button onClick={randomGift} >Quay Thuong</button>
    </div>
  );
}
export default App;
