// const MINUS_ACTION = 'down'

// // Create Reducer

// const reducer = (state, action) => {
//   console.log('checking running reducer')
//   switch (action) {
//     case PLUS_ACTION:
//       console.log(PLUS_ACTION)
//       return state + 1
//     case MINUS_ACTION:
//       return state - 1
//     default:
//       throw Error(`Invalid ${action}`)
//   }
// }

// Create InitState
const initState = {
  loading: false,
  data: [],
  error: null
}
// create Action
const GET_USER_REQUEST = 'GET_USER_REQUEST'
const GET_USER_SUCCESS = 'GET_USER_SUCCESS'
const GET_USER_ERROR = 'GET_USER_ERROR'
//create Reduce
const reducer = (state, action) => {
  switch (action.type) {
    case GET_USER_REQUEST:
      return {
        // tham so thu 1 la state hien tai
        ...state,
        loading: true
      }
    case GET_USER_SUCCESS:
      return {
        ...state,
        loading: false,
        data: action.data
      }
    case GET_USER_ERROR:
      return {
        ...state,
        loading: false,
        error: `Error ${action}`
      }

    default:
      throw Error(`Invalid ${action}`)
  }
}

function ChildComponent() {
  //----------------------------------------------------------------//
  //Code JS

  const [state, dispatchUserAction] = useReducer(reducer, initState)
  const getUser = () => {
    // Chay doan code Loading truoc khi request
    dispatchUserAction({
      type: GET_USER_REQUEST
    })
    // fetch API request
    // set Timeout 3000ms for regconize code
    setTimeout(() => {
      fetch('https://reqres.in/api/users')
        .then(res => res.json())
        .then(res => {
          dispatchUserAction({
            type: GET_USER_SUCCESS,
            data: res.data
          })
        })
        .catch(err => {
          dispatchUserAction({
            type: GET_USER_ERROR
          })
        })
    }, 4000)
  }

  //----------------------------------------------------------------//
  // Render UI
  //----------------------------------------------------------------//
  return (
    <div className="container">
      {state.loading ? (
        <p>Loading...</p>
      ) : (
        <p> {JSON.stringify(state)}</p>
      )}

      <Button onClick={getUser} variant="contained">
        GET USER
      </Button>
    </div>
  )
  //----------------------------------------------------------------//
}

export default ChildComponent
