import {
  GET_API_REQUEST,
  GET_API_SUCCESS,
  GET_API_ERROR
} from './constant'
export const initState = {
  loading: false,
  data: [],
  error: null
}

function Reducer(state, action) {
  switch (action.type) {
    case GET_API_REQUEST:
      return {
        ...initState,
        loading: true
      }
    case GET_API_SUCCESS:
      return {
        ...initState,
        loading: false,
        data: action.data
      }
    case GET_API_ERROR:
      return {
        ...initState,
        error: action.error
      }
    default:
      throw new Error(action.type + ' is not supported')
  }
}

export default Reducer
