import { useEffect, useState } from 'react'

const tabs = ['comments', 'posts', 'albums']
function Content() {
  const [post, setPost] = useState([])
  const [type, setType] = useState('comments')
  const [showtoTop, setShowtoTop] = useState(false)

  useEffect(() => {
    fetch(`https://jsonplaceholder.typicode.com/${type}`)
      .then(res => res.json())
      .then(data => {
        setPost(data)
      })
  }, [type])

  useEffect(() => {
    const handleScroll = () => {
      setShowtoTop(window.scrollY >= 200)
      console.log(showtoTop)
    }

    window.addEventListener('scroll', handleScroll)
  }, [])
  return (
    <div>
      {tabs.map(tabs => (
        <button onClick={() => setType(tabs)}> {tabs} </button>
      ))}

      <ul>
        {post.map(data => (
          <li>{data.title || data.name}</li>
        ))}
      </ul>
      {showtoTop && (
        <button
          style={{
            position: 'fixed',
            right: 10,
            bottom: 10
          }}
        >
          Go To Top
        </button>
      )}
    </div>
  )
}

export default Content
