import {
  useEffect,
  useState
} from 'react'

function Content() {
  const [avatar, setAvatar] =
    useState('')

  useEffect(() => {
    // Cleanup function
    return () => {
      avatar &&
        URL.revokeObjectURL(
          avatar.preview
        )
    }
  }, [avatar])
  const handleChange = e => {
    //create file to get avatar
    const file = e.target.files[0]
    file.preview =
      URL.createObjectURL(file)
    // setAvatar for avatarState
    setAvatar(file)
  }
  return (
    <div>
      <input
        type="file"
        onChange={handleChange}
      ></input>
      {avatar && (
        <img
          src={avatar.preview}
          alt=""
          width="80%"
        ></img>
      )}
    </div>
  )
}

export default Content
