import { useState } from 'react'
import Button from '@mui/material/Button'
import { Input, Radio, RadioGroup } from '@mui/material'

function App() {
  const storegeJobs = JSON.parse(localStorage.getItem('jobs'))
  const [job, setJob] = useState('')
  const [jobs, setJobs] = useState(storegeJobs ?? [])
  const handleSumbit = () => {
    setJobs(prev => {
      const listJobs = [...prev, job]
      const jsonJob = JSON.stringify(listJobs)
      localStorage.setItem('jobs', jsonJob)
      return listJobs
    })
  }
  const deleteElement = index => {
    storegeJobs.shift()
    localStorage.removeItem(index)
    console.log(storegeJobs)
  }

  return (
    <div style={{ padding: 35 }} className="App">
      <h1>Hello World</h1>
      <input value={job} onChange={e => setJob(e.target.value)} />
      <button onClick={handleSumbit}>Add</button>
      <ul>
        {jobs.map((job, index) => (
          <li>
            {job} <button onClick={deleteElement(index)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  )
}
export default App
