import { Button, Input } from '@mui/material'
import { useMemo, useReducer, useState, useEffect } from 'react'

function ChildComponent() {
  //----------------------------------------------------------------//
  //Code JS

  // Create initState
  const initState = 0

  // Create Action
  const PLUS_ACTION = 'up'
  const MINUS_ACTION = 'down'

  // Create Reducer
  const reducer = (state, action) => {
    switch (action) {
      case PLUS_ACTION:
        console.log(PLUS_ACTION)
        return state + 1
      case MINUS_ACTION:
        return state - 1
      default:
        throw Error(`Invalid ${action}`)
    }
  }
  useEffect(() => {
    const handleAction = action => {
      return dispatch(`${action}`)
    }
  }, [])

  const [state, dispatch] = useReducer(reducer, initState)

  //----------------------------------------------------------------//
  // Render UI
  //----------------------------------------------------------------//
  return (
    <div className="container">
      <p>{state}</p>
      <Button onClick={handleAction(PLUS_ACTION)} variant="contained">
        UP
      </Button>
      <Button
        onClick={handleAction(MINUS_ACTION)}
        className=""
        variant="contained"
      >
        DOWN
      </Button>
    </div>
  )
  //----------------------------------------------------------------//
}

export default ChildComponent
