import * as React from "react";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Typography from "@mui/material/Typography";
import {Button, CardActionArea, CardActions} from "@mui/material";
import {useStore} from "../../store";
import {useEffect} from "react";
import Rating from "@mui/material/Rating";
import Stack from "@mui/material/Stack";
import {
  GET_API_REQUEST,
  GET_API_SUCCESS,
  GET_API_ERROR,
} from "../../store/constant";

function Popular() {
  const [state, dispatch] = useStore();
  useEffect(() => {
    dispatch({
      type: GET_API_REQUEST,
    });
    fetch(
      "https://api.themoviedb.org/3/movie/popular?api_key=131f3dc4657440aa2ed80c0a22a7d1f9"
    )
      .then((res) => res.json())
      .then((res) => {
        dispatch({
          type: GET_API_SUCCESS,
          data: res,
        });
      })
      .catch((err) => {
        dispatch({
          type: GET_API_ERROR,
        });
      });
  }, []);

  return (
    <div>
      <div>
        <h1 className="text-4xl font-semibold">What's Popular</h1>
      </div>
      <div className="mt-8 mb-12 flex  ">
        {(state.data.results || []).map(
          (value, key) =>
            key < 7 && (
              <Card
                key={key}
                className="ml-4 static transition ease-in-out delay-150 hover:-translate-y-1 hover:scale-110  duration-300"
                sx={{maxWidth: 355}}
              >
                <CardActionArea>
                  <CardMedia
                    component="img"
                    height="140"
                    image={
                      "https://www.themoviedb.org/t/p/w600_and_h900_bestv2" +
                      value.poster_path
                    }
                    alt="green iguana"
                  />
                  <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                      {value.original_title}
                    </Typography>
                    <Stack spacing={1}>
                      <Rating
                        name="half-rating-read"
                        defaultValue={value.vote_average / 2}
                        precision={0.5}
                        readOnly
                        size="medium"
                      />
                    </Stack>
                    <Typography
                      className="font-semibold "
                      variant="body2"
                      color="text.secondary"
                    >
                      {value.release_date}
                    </Typography>
                  </CardContent>
                </CardActionArea>
              </Card>
            )
        )}
      </div>
    </div>
  );
}

export default Popular;
