import Button from '@mui/material/Button'
import { useStore } from './store'
import {
  GET_API_REQUEST,
  GET_API_SUCCESS,
  GET_API_ERROR
} from './store/constant'

function App() {
  const [state, dispatch] = useStore()
  const newData = []
  console.log(state)
  const handleGetApi = () => {
    dispatch({
      type: GET_API_REQUEST
    })
    fetch('https://reqres.in/api/users')
      .then(res => res.json())
      .then(res => {
        dispatch({
          type: GET_API_SUCCESS,
          data: res.data
        })
      })
      .catch(err => {
        dispatch({
          type: GET_API_ERROR
        })
      })
  }
  return (
    <section className=" h-screen bg-gradient-to-b from-slate-600 to-purple-700">
      <div style={{ padding: 35 }} className="container ">
        <h1>App Hello World</h1>
        {/* <p> {JSON.stringify(state)}</p> */}

        <div className="columns-3xs">
          <Button variant="contained" onClick={handleGetApi}>
            HANDLE
          </Button>
          {console.log('state dinh dang :', typeof state.data)}

          {state.data.map((data, key) => (
            <div>
              <h1 className=" text-2xl" key={key}>
                {data.email}
              </h1>
              <img src={data.avatar}></img>
            </div>
          ))}
        </div>
      </div>
    </section>
  )
}
export default App
