import { Button, Input } from '@mui/material'
import { useMemo, useReducer, useState, useEffect } from 'react'

// Create initState
const initState = 0

// Create Action
const PLUS_ACTION = 'up'
const MINUS_ACTION = 'down'

// Create Reducer

const reducer = (state, action) => {
  console.log('checking running reducer')
  switch (action) {
    case PLUS_ACTION:
      console.log(PLUS_ACTION)
      return state + 1
    case MINUS_ACTION:
      return state - 1
    default:
      throw Error(`Invalid ${action}`)
  }
}

function ChildComponent() {
  //----------------------------------------------------------------//
  //Code JS

  const [state, dispatch] = useReducer(reducer, initState)

  //----------------------------------------------------------------//
  // Render UI
  //----------------------------------------------------------------//
  return (
    <div className="container">
      <p>{state}</p>
      <Button
        onClick={() => dispatch(PLUS_ACTION)}
        variant="contained"
      >
        UP
      </Button>
      <Button
        onClick={() => dispatch(MINUS_ACTION)}
        className=""
        variant="contained"
      >
        DOWN
      </Button>
    </div>
  )
  //----------------------------------------------------------------//
}

export default ChildComponent
