import * as React from "react";
import TextField from "@mui/material/TextField";
import Autocomplete from "@mui/material/Autocomplete";
import {useState, useEffect} from "react";
function MovieSearch() {
  const [searchState, setSearch] = useState([]);
  const [dataSearch, setData] = useState([]);
  const handleSearch = () => {
    fetch(
      `https://api.themoviedb.org/3/search/movie?api_key=131f3dc4657440aa2ed80c0a22a7d1f9&language=en-US&page=1&include_adult=false&query=${searchState}`
    )
      .then((res) => res.json())
      .then((res) => {
        setData(res);
      })
      .catch((err) => {});
    console.log(dataSearch);
    console.log(searchState);
  };

  return (
    <div className="flex flex-row">
      <TextField
        className="rounded-2xl"
        sx={{width: 300}}
        value={searchState}
        onChange={(e) => setSearch(e.target.value)}
        label="Movie"
        placeholder="Search for a movie"
      />

      <button onClick={handleSearch}>
        <a className="ml-4 transition ease-in-out delay-150 bg-teal-600 hover:-translate-y-1 hover:scale-110 hover:bg-teal-600 duration-300 inline-block text-center  border border-transparent rounded-md py-3 px-8 font-medium text-white ">
          SEARCH
        </a>
      </button>
    </div>
  );
}

export default MovieSearch;

